import React, { useState, useEffect } from 'react'
import "./MovieDetails.scss"
import YoutubeEmbed from '../../components/YoutubeEmbed/YoutubeEmbeded'
import Carousel from "../../components/Carousel/Carousel"
import axios from 'axios'

export default function MovieDetails() {
    const trailer = "";
    const [Carousels, setCarousels] = useState([]);
    const [genre, setGenre] = useState(null);

    useEffect(() => {
        const getRandomCarousels = async () => {
            try {
                const res = await axios.get(`http://localhost:4000/api/carousels${genre ? "genre=" + genre : ""}`);
                setCarousels(res.data);
            } catch (err) {
                console.log(err);
            }
        };
        getRandomCarousels();
    }, [genre])
    return (
        <div className='movieDetails'>
            <div className="top">
                <img className='left' src="https://www.wwe.com/f/styles/wwe_large/public/all/2019/10/RAW_06202016rf_1606--3d3997f53e6f3e9277cd5a67fbd8f31f.jpg" alt="" />
                <div className="right">
                    <YoutubeEmbed className = 'rightTrailer'embedId={trailer} />
                </div>
            </div>

            <div className="desc">
                Lorem, ipsum dolor sit amet consectetur adipisicing elit. Culpa ad amet magnam non sed sunt ullam, omnis maxime expedita accusantium animi. Harum, corrupti! Non, molestiae perferendis. Dolores id cum rerum?
            </div>

            <div className="carousel">
                {/* <Carousel genre="Drama" /> */}
            </div>

            <div className="comments">
            </div>

        </div>
    )
}
