import React, { useState } from 'react'
import "../YoutubeEmbed/YoutubeEmbeded"
import YoutubeEmbed from '../YoutubeEmbed/YoutubeEmbeded';
import "./Card.scss";

export default function Card({ index }) {
    const [isHovered, setIsHovered] = useState(false);
    const trailer = "m01YktiEZCw";

    return (
        <div className='card'
            style={{ left: isHovered && index * 225 - 50 + index * 2.5 }}
            onMouseEnter={() => { setIsHovered(true) }}
            onMouseLeave={() => { setIsHovered(false) }}>
            {isHovered ? (
                <>
                    <YoutubeEmbed embedId={trailer} />

                    <div className="cardInfo">
                            <span>1hour 52 mins</span>
                            <span className='rating'>4.5‚≠ê</span>
                            <span>1954</span>
                        
                    </div>
                    <div className="desc">
                        A wheelchair-bound photographer spies on his neighbors from his Greenwich Village courtyard apartment window,
                        and becomes convinced one of them has committed murder, despite the skepticism of his fashion-model girlfriend.
                    </div>
                    <div className="genre">
                        Thriller
                    </div>
                </>
            ):<img src="https://prod-images.tcm.com/Master-Profile-Images/thegoodthebadandtheugly1968.18724.jpg"
            alt="" />}
        </div>
    )
}
