import React, { useEffect, useRef, useState } from 'react';
import "./HomePage.scss";
import Footer from "../../components/Footer/Footer";
import HeroCard from "../../components/HeroCard/HeroCard";
import NavBar from '../../components/NavBar/NavBar';
import Carousel from '../../components/Carousel/Carousel';
import axios from "axios";
// import { GetHomePageCarousels, UpdateAll } from '../../utils/apiCalls';

function HomePage() {
    const [Carousels, setCarousels] = useState([]);
    const [loading, setLoading] = useState(false);
    // useEffect(() => {
    //     const getRandomCarousels = async () => {
    //         try {
    //             const res = await axios.get(`http://localhost:4000/api/carousels${genre ? "genre=" + genre : ""}`);
    //             console.log(res.data);
    //             setCarousels(res.data);
    //         } catch (err) {
    //             console.log(err);
    //         }
    //     };
    //     getRandomCarousels();
    // }, [genre])
    const {current: genres} = useRef(["Newest", "BestRated"]);
    useEffect(() => {
        const getCarousels = async () => {
            setLoading(true);
            for (const genre of genres) {
                try {
                    const res = await axios.get(`http://localhost:4000/api/carousels/` + genre);
                    setCarousels([...Carousels, res.data]);
                } catch (err) {
                    console.log(err);
                }
            }
            setLoading(false);
        }
        getCarousels();
    },[]);
    return (
        <div className='Home'>
            <NavBar />
            <HeroCard />
            {!loading && Carousels.map((carousel) => (
                <Carousel key={carousel._id} carousel={carousel} />
            ))}
            <Footer />
        </div>
    );
}

export default HomePage;
