import axios from "axios";
import { useState, useEffect } from "react";

const GetBest = () =>{
    UpdateBest();
    const [Carousel, setCarousel] = useState([]);
    useEffect(() => {
        const getBestCarousel = async () => {
            try {
                const res = await axios.get(`http://localhost:4000/api/carousels/"genre=BestRated"`);
                setCarousel(res.data);
            } catch (err) {
                console.log(err);
            }
        };
        getBestCarousel();
    },[])
}
const UpdateBest = () => {
    const [Carousel, setCarousel] = useState([])

    useEffect(() => {
        const updateBestCarousel = async () => {
            try {
                const res = await axios.put(`http://localhost:4000/api/carousels/rating`);
                setCarousel(res.data);
            } catch (err) {
                console.log(err);
            }
        };
        updateBestCarousel();
    })

}

const UpdateNewest = () => {
    const [Carousel, setCarousel] = useState([])

    useEffect(() => {
        const updateBestCarousel = async () => {
            try {
                const res = await axios.put(`http://localhost:4000/api/carousels/newest`);
                setCarousel(res.data);
            } catch (err) {
                console.log(err);
            }
        };
        updateBestCarousel();
    })

}

export {
    UpdateBest,
    GetBest
}
