import './HeroCard.css';
import {React} from 'react';
import {Link} from 'react-router-dom';



function HeroCard(){
    return (
		<div
			className="hero-container"
			style={{
				backgroundSize: 'cover',
				backgroundImage: `url("https://filmehd.se/posters/6ovtTxnNx5P9QkxHQ7iRXsNoVgA.jpg")`,
				backgroundPosition: 'center center',
			}}
		>
			<h1>Casablanca</h1>
			<p className="movie-description">A cynical expatriate American cafe owner struggles to decide whether or not to help his former lover and her fugitive husband escape the Nazis in French Morocco.</p>
			<div className="hero-btns">
				<Link to={'/moviedetail/'} className="navigation-btns" id="more-info-btn">
					More Info
				</Link>
			</div>
		</div>
	);
}
export default HeroCard;
