import "./NavBar.scss";
import LogoutIcon from '@mui/icons-material/Logout';
import SearchIcon from '@mui/icons-material/Search';
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react'
import { getItemValue, setItemValue } from '../../utils/localStorageUtils';
import axios from "axios";
function NavBar() {
    const [isScrolled, setIsScrolled] = useState(false);
    const [movie, setMovie] = useState({});

    window.onscroll = () => {
        setIsScrolled(window.pageYOffset === 0 ? false : true);
        return () => window.onscroll = null;
    }
    const refreshPage = () =>{
        if(getItemValue('reloadCount') < 2) {
            setItemValue('reloadCount', getItemValue('reloadCount')+1)
            window.location.reload();
          } else {
            setItemValue('reloadCount', 0);
          }
    }

	useEffect(() => {
		const getMovie = async () => {
			try {
				const res = await axios.get(`http://localhost:4000/api/movies/random`, {
				});
				setMovie(res.data[0]);
			} catch (err) {
				console.log(err);
			}
		}
		getMovie()
	},[])

    return (
        <div className={isScrolled ? "navbar scrolled" : "navbar"}>
            <div className="container">
                <div className="left">
                    <img
                        src="https://as1.ftcdn.net/v2/jpg/02/05/18/44/1000_F_205184418_t91TINxilW8CT2aWEqVW9J8b6CKf8iss.jpg"
                        alt=""
                    />
                    <Link to="/" className="link">
                        <span>HOME</span>
                    </Link>

                    <Link to="/categories" className="link">
                        <span>CATEGORIES</span>
                    </Link>
                    <Link onClick = {refreshPage} to={"/moviedetail"} state={{ movie:movie }} className='link'>
                        <span>RANDOM</span>
                    </Link>
                </div>
                <div className="right">
                    <SearchIcon className="icon Search" />
                    <LogoutIcon className='icon' />
                    <Link to="/login" className="link">
                        <span>LOG OUT</span>
                    </Link>
                </div>
            </div>
        </div>
    )
}

export default NavBar
