import React, { useEffect, useState } from 'react';
import "./HomePage.scss";
import Footer from "../../components/Footer/Footer";
import HeroCard from "../../components/HeroCard/HeroCard";
import NavBar from '../../components/NavBar/NavBar';
import Carousel from '../../components/Carousel/Carousel';
import axios from "axios";
import { GetHomePageCarousels, UpdateAll } from '../../utils/apiCalls';

function HomePage() {
    const [Carousels, setCarousels] = useState([]);
    // const [genre, setGenre] = useState(null);
    // UpdateAll();
    // useEffect(() => {
    //     const getRandomCarousels = async () => {
    //         try {
    //             const res = await axios.get(`http://localhost:4000/api/carousels${genre ? "genre=" + genre : ""}`);
    //             setCarousels(res.data);
    //         } catch (err) {
    //             console.log(err);
    //         }
    //     };
    //     getRandomCarousels();
    // }, [genre])
    setCarousels(GetHomePageCarousels());
    return (
        <div className='Home'>
            <NavBar />
            <HeroCard />
            {Carousels.map((carousel) => (
                <Carousel key={carousel._id} carousel={carousel} />
            ))}
            <Footer />
        </div>
    );
}

export default HomePage;
