import axios from "axios";
import { useState, useEffect } from "react";

const UpdateAll = () => {
    UpdateBest();
    UpdateNewest();
}
const UpdateBest = () => {
    const [Carousel, setCarousel] = useState([])

    useEffect(() => {
        const updateBestCarousel = async () => {
            try {
                const res = await axios.put(`http://localhost:4000/api/carousels/rating`).then(
                );
                setCarousel(res.data);
            } catch (err) {
                console.log(err);
            }
        };
        updateBestCarousel();
    })

}

const GetHomePageCarousels = () => {
    UpdateAll();
    const [Carousels, setCarousels] = useState([]);
    // const [genre, setGenre] = useState(null);
    useEffect(() => {
        const genres = ["BestRated", "Newest"];
        const getCarousels = () => {
            genres.forEach(genre => {
                try {
                    const res = axios.get(`http://localhost:4000/api/carousels${genre ? "genre=" + genre : ""}`);
                    setCarousels([...Carousels, res.data]);
                    console.log(Carousels);
                } catch (err) {
                    console.log(err);
                }
            });
        }
        getCarousels();
    }, [Carousels]);
}

const UpdateNewest = () => {
    const [NewestCarousel, setNewestCarousel] = useState([])

    useEffect(() => {
        const updateNewestCarousel = async () => {
            try {
                const res = await axios.put(`http://localhost:4000/api/carousels/newest`);
                setNewestCarousel(res.data);
            } catch (err) {
                console.log(err);
            }
        };
        updateNewestCarousel();
    })

}

export {
    GetHomePageCarousels
}
