import './HeroCard.scss';
import { React, useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';


function HeroCard() {
	const [movie, setMovie] = useState(null);

	useEffect(() => {
		const getMovie = async () => {
			try {
				const res = await axios.get(`http://localhost:4000/api/movies/random`, {
				});
				setMovie(res.data);
			} catch (err) {
				console.log(err);
			}
		}
		getMovie()
	}, [])

	return (
		<div
			className="hero-container"
			style={{
				backgroundSize: 'cover',
				// backgroundImage: `url("https://m.media-amazon.com/images/M/MV5BODE4ZDJiNTYtN2ViYy00OGE0LThhMjItNzA2NjI4NjA1MDAyXkEyXkFqcGdeQXVyMjMzMDI4MjQ@._V1_SX1777_CR0,0,1777,962_AL_.jpg")`,
				backgroundImage: `url("https://static1.colliderimages.com/wordpress/wp-content/uploads/2022/03/the-godfather-character-guide.jpg?q=50&fit=contain&w=1500&h=&dpr=1.5")`,
				// backgroundImage: `url(${movie.img})`,
				backgroundPosition: 'center center',
			}}
		>
			<h1>{movie.title}</h1>
			<p className="movie-description">A cynical expatriate American cafe owner struggles to decide whether or not to help his former lover and her fugitive husband escape the Nazis in French Morocco.</p>
			<div className="hero-btns">

				<Link to={'/moviedetail/'} className="navigation-btns" id="more-info-btn">
					More Info
				</Link>
			</div>
		</div>
	);
}
export default HeroCard;
