import React, { useEffect, useState } from 'react';
import "./HomePage.scss";
import Footer from "../../components/Footer/Footer";
import HeroCard from "../../components/HeroCard/HeroCard";
import NavBar from '../../components/NavBar/NavBar';
import Carousel from '../../components/Carousel/Carousel';
import axios from "axios";
// import { GetHomePageCarousels, UpdateAll } from '../../utils/apiCalls';

function HomePage() {
    const [Carousels, setCarousels] = useState([]);
    const genres = ["BestRated", "Newest"];
    // useEffect(() => {
    //     const getRandomCarousels = async () => {
    //         try {
    //             const res = await axios.get(`http://localhost:4000/api/carousels${genre ? "genre=" + genre : ""}`);
    //             console.log(res.data);
    //             setCarousels(res.data);
    //         } catch (err) {
    //             console.log(err);
    //         }
    //     };
    //     getRandomCarousels();
    // }, [genre])
    useEffect(() => {
        try {
            const fetchCarouselPromises = genres.map(genre =>
                axios.get(`http://localhost:4000/api/carousels/` + genre)
            )
            Promise.all(fetchCarouselPromises).then((values) => {
                setCarousels(prev => [...prev, ...values.map(value => value.data)]);
            });
        } catch (err) {
            console.log(err);
        }
    });
    return (
        <div className='Home'>
            <NavBar />
            <HeroCard />
            {Carousels.map((carousel) => (
                <Carousel key={carousel._id} carousel={carousel} />
            ))}
            <Footer />
        </div>
    );
}

export default HomePage;
